mapper.select:
select v.id as v_id, v.student_id as v_student_id, v.reservation_id as v_reservation_id, v.violation_type as v_violation_type, v.violation_desc as v_violation_desc, v.violation_time as v_violation_time, v.record_type as v_record_type, v.admin_id as v_admin_id, v.status as v_status, s.id as s_id, s.student_number as s_student_number, s.student_name as s_student_name, s.major_name as s_major_name from violation_record v left join student s on s.id = v.student_id
and s.student_number = #{studentNumber}
and v.violation_type = #{violationType}
and v.record_type = #{recordType}
and v.status = #{status}
and DATE(v.violation_time) >= #{startDate}
and DATE(v.violation_time) <= #{endDate}
order by v.violation_time desc
